import Cookies from "universal-cookie";

const initState = {
  todo: "651651",
  savedValues: [],
  error: ""
};

const reducer = (state = initState, action) => {
  function nextweek() {
    let today = new Date();
    let nextweek = new Date(
      today.getFullYear(),
      today.getMonth(),
      today.getDate() + 7
    );
    return nextweek;
  }
  let cookie = new Cookies();
  if (cookie.get("todo")) {
    cookie = cookie.get("todo");
  } else {
    console.log("2");
    cookie.set("todo", initState, {
      path: "/",
      expires: nextweek()
    });
    cookie = cookie.get("todo");
  }

  const type = action.type;
  const value = action.value;

  switch (type) {
    case "ADD":
      state = {
        ...state,
        todo: value
      };
      break;

    case "REMOVE":
      let array = state.savedValues.filter(x => {
        return x !== value;
      });
      state = {
        ...state,
        savedValues: array
      };

      break;
    case "SAVE":
      if (state.todo !== "" && !state.savedValues.includes(state.todo)) {
        state = {
          ...state,
          savedValues: state.savedValues.concat(state.todo),
          error: ""
        };
      } else {
        state = {
          ...state,
          error: "You already have a todo named: " + state.todo
        };
      }

      break;
  }
  cookie.set("todo", "sdfsdfsdfsdf", {
    path: "/",
    expires: nextweek()
  });
  return state;
};

export default reducer;
