import React from "react";
import { useSelector, useDispatch } from "react-redux";
import styled from "styled-components";
import { SingleTodo } from "./SingleTodo";

import DragThis from "../scripts/Draggable";

const Li = styled.div`
  padding: 15px;
  text-align: left;
  width: 50vw;
  margin: auto;
  border-radius: 10px;
  cursor: pointer;
  &:hover {
    background-color: #8fa9a3;
    color: #c6d2cf;
  }
`;
const Error = styled.div`
  padding: 50px;
  background-color: #ffe7d9;
`;
function dragElement(elmnt) {
  var pos1 = 0,
    pos2 = 0,
    pos3 = 0,
    pos4 = 0;
  if (document.getElementById(elmnt.id)) {
    // if present, the header is where you move the DIV from:
    document.getElementById(elmnt.id).onmousedown = dragMouseDown;
  } else {
    // otherwise, move the DIV from anywhere inside the DIV:
    elmnt.onmousedown = dragMouseDown;
  }

  function dragMouseDown(e) {
    e = e || window.event;
    e.preventDefault();
    // get the mouse cursor position at startup:
    pos3 = e.clientX;
    pos4 = e.clientY;
    document.onmouseup = closeDragElement;
    // call a function whenever the cursor moves:
    document.onmousemove = elementDrag;
  }

  function elementDrag(e) {
    e = e || window.event;
    e.preventDefault();
    // calculate the new cursor position:
    pos1 = pos3 - e.clientX;
    pos2 = pos4 - e.clientY;
    pos3 = e.clientX;
    pos4 = e.clientY;
    // set the element's new position:
    elmnt.style.top = elmnt.offsetTop - pos2 + "px";
    elmnt.style.left = elmnt.offsetLeft - pos1 + "px";
  }

  function closeDragElement() {
    // stop moving when mouse button is released:
    document.onmouseup = null;
    document.onmousemove = null;
  }
}

const DisplayToDos = () => {
  const dispatch = useDispatch();
  const error = useSelector(state => state.error);
  const savedValues = useSelector(state => state.savedValues);
  const remove = val => dispatch({ type: "REMOVE", value: val });

  const Update = (changedFrom, changedTo) =>
    dispatch({ type: "EDIT", value: changedTo, changedFrom: changedFrom });

  const showTodo = savedValues
    ? savedValues.map(x => {
        const key = Math.random();

        let shit = (
          <DragThis id={key}>
            <SingleTodo
              remove={remove}
              key={key}
              x={x}
              changeHandler={Update}
              oldValue={x}
            />
          </DragThis>
        );

        dragElement(document.getElementById(key));

        return shit;
      })
    : null;

  const showError = error !== "" ? <Error>{error}</Error> : null;

  return (
    <>
      {showError}
      <div>{showTodo}</div>
    </>
  );
};

export default DisplayToDos;
