import React from "react";
import styled from "styled-components";
import Edit from "./Edit";
const DeleteThis = styled.span`
  color: #c6d2cf;
  float: right;
`;

const Li = styled.div`
  padding: 15px;
  text-align: left;
  width: 50vw;
  margin: auto;
  border-radius: 10px;
  cursor: pointer;
  &:hover {
    background-color: #8fa9a3;
    color: #c6d2cf;
  }
`;
const Error = styled.div`
  padding: 50px;
  background-color: #ffe7d9;
`;
const EditThis = styled.span`
  color: #c6d2cf;
  float: right;
`;
export const SingleTodo = props => {
  return (
    <Li key={props.key}>
      <span>{props.x}</span>
      <DeleteThis onClick={() => remove(props.x)}>Delete?</DeleteThis>
      <Edit changeHandler={props.Update} oldValue={props.x} />
    </Li>
  );
};
