import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { DefaultEditor } from "react-simple-wysiwyg";

const Edit = props => {
  const dispatch = useDispatch();
  let showEditor = props.showEditor;
  let oldValue = props.oldValue;

  const [value, setValue] = React.useState(oldValue);

  const Update = (changedFrom, changedTo) =>
    dispatch({ type: "EDIT", value: changedTo, changedFrom: changedFrom });
  Update(oldValue, value);

  const showThis = (showEditor, oldValue) => {
    return showEditor ? (
      <DefaultEditor value={value} onChange={e => setValue(e.target.value)} />
    ) : null;
  };

  return showThis(showEditor, oldValue);
};
export default Edit;
