import React from "react";
import styled from "styled-components";
import { connect } from "react-redux";

const NameTitle = styled.h1`
  background-color: #aa7939;
`;
const AddButton = styled.a`
  background-color: #aa7939;
  padding: 30px;
  border-radius: 65px;
`;
const CreateToDo = () => {
  return (
    <div>
      <NameTitle>To Do</NameTitle>
      <input type="text"></input>
      <AddButton href="#">+</AddButton>
    </div>
  );
};

const mapStateToProps = state => {
  return {
    todo: state.todo,
    savedValues: state.savedValues
  };
};

const mapDispatchToProps = dispatch => {
  return {
    onPositiveCounter: () => dispatch({ type: "ADD", value: state.todo }),
    onNegativeCounter: () => dispatch({ type: "REMOVE", value: state.todo }),
    onSave: () => dispatch({ type: "SAVE" })
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(CreateToDo);
