import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { DefaultEditor } from "react-simple-wysiwyg";

const Edit = props => {
  const dispatch = useDispatch();
  let showEditor = props.showEditor;
  let oldValue = props.oldValue;

  const [html, setHtml] = React.useState("my <b>HTML</b>");

  const Update = (changedFrom, changedTo) =>
    dispatch({ type: "EDIT", value: changedTo, changedFrom: changedFrom });

  const showThis = (showEditor, oldValue) => {
    return showEditor ? (
      <DefaultEditor
        value={oldValue}
        onChange={e => Update(oldValue, e.target.value)}
      />
    ) : null;
  };

  return showThis;
};
export default Edit;
