const initState = {
  todo: "",
  savedValues: []
};

const reducer = (state = initState, action) => {
  const type = action.type;
  const value = action.value ? action.value : "";

  console.log("TYPE", type);
  console.log("VALUE", value);
  console.log("STATE REDUCER", state);

  switch (type) {
    case "ADD":
      if (state.savedValues.includes(value) !== -1 && value !== "") {
        return {
          ...state,
          todo: value
        };
      }

    case "REMOVE":
      let array = state.savedValues.filter(x => {
        return x !== value;
      });
      return {
        ...state,
        savedValues: array
      };

    case "SAVE":
      if (state.todo !== "") {
        return {
          ...state,
          savedValues: state.savedValues.concat(state.todo)
        };
      }
  }

  return state;
};

export default reducer;
