import React, { useState } from "react";
import styled from "styled-components";
import { connect } from "react-redux";

const NameTitle = styled.h1`
  background-color: #aa7939;
`;
const AddButton = styled.a`
  background-color: #aa7939;
  padding: 30px;
  border-radius: 65px;
`;
const CreateToDo = props => {
  return (
    <div>
      <NameTitle>To Do</NameTitle>
      <input
        value={props.todo}
        onChange={e => props.onAdd(e.target.value)}
        type="text"
      ></input>
      <AddButton href="#" onClick={() => props.onAdd()}>
        +
      </AddButton>
    </div>
  );
};

const mapStateToProps = state => {
  return {
    todo: state.todo,
    savedValues: state.savedValues
  };
};

const mapDispatchToProps = (dispatch, state) => {
  return {
    onAdd: () => dispatch({ type: "ADD", value: "sd" }),
    onRemove: () => dispatch({ type: "REMOVE", value: 5 }),
    onSave: () => dispatch({ type: "SAVE" })
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(CreateToDo);
