import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { DefaultEditor } from "react-simple-wysiwyg";

const Edit = props => {
  const dispatch = useDispatch();
  let showEditor = props.showEditor;
  let oldValue = props.oldValue;

  const [value, setValue] = React.useState(oldValue);

  const showThis = (showEditor, oldValue) => {
    return showEditor ? (
      <input
        type="text"
        value={value}
        onChange={e => {
          props.changeHandler(oldValue, e.target.value);
        }}
      />
    ) : null;
  };

  return showThis(showEditor, oldValue);
};
export default Edit;
