import React from "react";
import { connect } from "react-redux";

const DisplayToDos = props => {
  const todo = props.savedValues
    ? props.savedValues.map(x => {
        const key = Math.random();
        return (
          <li onClick={() => handleDelete(x)} key={key}>
            {x}
          </li>
        );
      })
    : null;

  const handleDelete = x => {
    /*const savedValues = props.savedValues;
    const indexToRemove = savedValues.findIndex(x);
    console.log(array.splice(indexToRemove, indexToRemove));*/
    props.onRemove();
  };
  return <ul>{todo}</ul>;
};

const mapStateToProps = state => {
  return {
    savedValues: state.savedValues
  };
};

const mapDispatchToProps = dispatch => {
  return {
    onRemove: () => dispatch({ type: "REMOVE", value: 5 })
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(DisplayToDos);
