import Cookies from "universal-cookie";

let cookie = new Cookies("todo");

const initState = {
  todo: "",
  savedValues: [],
  error: ""
};

if (cookie.get("todo")) {
  cookie.set("todo", JSON.stringify(initState), { path: "/" });
  cookie = cookie.get("todo");
} else {
  cookie = cookie.get("todo");
}

const reducer = (state = cookie, action) => {
  const type = action.type;
  const value = action.value;

  switch (type) {
    case "ADD":
      return {
        ...state,
        todo: value
      };

    case "REMOVE":
      let array = state.savedValues.filter(x => {
        return x !== value;
      });
      return {
        ...state,
        savedValues: array
      };

    case "SAVE":
      if (state.todo !== "" && !state.savedValues.includes(state.todo)) {
        return {
          ...state,
          savedValues: state.savedValues.concat(state.todo),
          error: ""
        };
      } else {
        return {
          ...state,
          error: "You already have a todo named: " + state.todo
        };
      }
  }

  return state;
};

export default reducer;
