import React from "react";
import styled from "styled-components";
import Edit from "./Edit";
import { Markup } from "interweave";

const DeleteThis = styled.span`
  color: #c6d2cf;
`;

const Li = styled.div`
  padding: 15px;
  text-align: left;
  width: 50vw;
  margin: auto;
  border-radius: 10px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  cursor: pointer;
  &:hover {
    background-color: #8fa9a3;
    color: #c6d2cf;
  }
`;
const Error = styled.div`
  padding: 50px;
  background-color: #ffe7d9;
`;
const EditThis = styled.span`
  color: #c6d2cf;
  float: right;
`;
export const SingleTodo = props => {
  const [editing, setEditing] = React.useState(false);

  return (
    <Li onClick={() => setEditing(true)} key={props.key}>
      <span>
        <Markup content={props.x} />{" "}
      </span>
      <DeleteThis onClick={() => props.remove(props.x)}>Delete?</DeleteThis>
      <Edit
        style={{ width: "100%" }}
        changeHandler={props.changeHandler}
        showEditor={editing}
        oldValue={props.x}
      />
    </Li>
  );
};
