import Cookies from "universal-cookie";

const initState = {
  todo: "start stateer hei",
  savedValues: [],
  error: ""
};

function nextweek() {
  let today = new Date();
  let nextweek = new Date(
    today.getFullYear(),
    today.getMonth(),
    today.getDate() + 7
  );
  return nextweek;
}
let cookie = new Cookies();

if (cookie.get("todo")) {
  console.log("1 ");
  cookie = cookie.get("todo");
  //JSON.parse(cookie));
} else {
  console.log("2");

  cookie.set("todo", initState, {
    path: "/",
    expires: nextweek()
  });
  cookie = cookie.get("todo");
}

const reducer = (state = cookie, action) => {
  console.log("COOKCIE", state);
  const type = action.type;
  const value = action.value;

  switch (type) {
    case "ADD":
      state = {
        ...state,
        todo: value
      };

      break;

    case "REMOVE":
      let array = state.savedValues.filter(x => {
        return x !== value;
      });
      state = {
        ...state,
        savedValues: array
      };

      break;
    case "SAVE":
      if (state.todo !== "" && !state.savedValues.includes(state.todo)) {
        state = {
          ...state,
          savedValues: state.savedValues.concat(state.todo),
          error: ""
        };
      } else {
        state = {
          ...state,
          error: "You already have a todo named: " + state.todo
        };
      }

      break;
  }
  document.cookie =
    "todo" + "=" + JSON.stringify(state) + ";" + nextweek() + ";path=/";

  console.log(document.cookie);
  return state;
};

export default reducer;
