import React from "react";
import { connect } from "react-redux";

const DisplayToDos = props => {
  const todo = props.savedValues
    ? props.savedValues.map(x => {
        const key = Math.random();
        return (
          <li onClick={() => props.onRemove(x)} key={key}>
            {x}
          </li>
        );
      })
    : null;

  const showThis = props.error !== "" ? <div>{props.error}</div> : null;

  return (
    <>
      <ul>
        {showThis}
        {todo}
      </ul>
    </>
  );
};

const mapStateToProps = state => {
  return {
    savedValues: state.savedValues,
    error: state.error
  };
};

const mapDispatchToProps = dispatch => {
  return {
    onRemove: val => dispatch({ type: "REMOVE", value: val })
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(DisplayToDos);
