import React from "react";
import { useSelector, useDispatch } from "react-redux";
import styled from "styled-components";
import { SingleTodo } from "./SingleTodo";

import DragThis from "../scripts/Draggable";

const Li = styled.div`
  padding: 15px;
  text-align: left;
  width: 50vw;
  margin: auto;
  border-radius: 10px;
  cursor: pointer;
  &:hover {
    background-color: #8fa9a3;
    color: #c6d2cf;
  }
`;
const Error = styled.div`
  padding: 50px;
  background-color: #ffe7d9;
`;

const DisplayToDos = () => {
  const dispatch = useDispatch();
  const error = useSelector(state => state.error);
  const savedValues = useSelector(state => state.savedValues);
  const remove = val => dispatch({ type: "REMOVE", value: val });

  const Update = (changedFrom, changedTo) =>
    dispatch({ type: "EDIT", value: changedTo, changedFrom: changedFrom });

  const showTodo = savedValues
    ? savedValues.map(x => {
        const key = Math.random();

        return (
          <DragThis id={key}>
            <SingleTodo
              remove={remove}
              key={key}
              x={x}
              changeHandler={Update}
              oldValue={x}
            />
          </DragThis>
        );
      })
    : null;

  const showError = error !== "" ? <Error>{error}</Error> : null;

  return (
    <>
      {showError}
      <div>{showTodo}</div>
    </>
  );
};

export default DisplayToDos;
