import React from "react";
import { useSelector, useDispatch } from "react-redux";
import styled from "styled-components";
import { DefaultEditor } from "react-simple-wysiwyg";

const Li = styled.div`
  padding: 15px;
  text-align: left;
  width: 50vw;
  margin: auto;
  border-radius: 10px;
  cursor: pointer;
  &:hover {
    background-color: #8fa9a3;
    color: #c6d2cf;
  }
`;
const Error = styled.div`
  padding: 50px;
  background-color: #ffe7d9;
`;

const DeleteThis = styled.span`
  color: #c6d2cf;
  float: right;
`;
const DisplayToDos = () => {
  const [html, setHtml] = React.useState("my <b>HTML</b>");

  const onChange = e => {
    setHtml(e.target.value);
  };

  const dispatch = useDispatch();
  const error = useSelector(state => state.error);
  const savedValues = useSelector(state => state.savedValues);

  const remove = val => dispatch({ type: "REMOVE", value: val });
  const Editor = props => {
    <DefaultEditor value={value} onChange={onChange} />;
  };
  const showTodo = savedValues
    ? savedValues.map(x => {
        const key = Math.random();

        return (
          <Li key={key}>
            <span>{x}</span>
            <DeleteThis onClick={() => remove(x)}>Delete?</DeleteThis>
          </Li>
        );
      })
    : null;

  const showError = error !== "" ? <Error>{error}</Error> : null;

  return (
    <>
      {showError}
      <ul>{showTodo}</ul>
    </>
  );
};

export default DisplayToDos;
