const initState = {
  todo: "",
  savedValues: []
};

const reducer = (state = initState, action) => {
  const type = action.type;
  const value = action.value ? action.value : 1;
  switch (type) {
    case "ADD":
      return {
        ...state,
        todo: state.todo + "  t  t"
      };

    case "REMOVE":
      return {
        ...state,
        todo: state.todo + "  t  t"
      };

    case "SAVE":
      return {
        ...state,
        savedValues: state.savedValues.concat(state.counter)
      };
  }

  return state;
};

export default reducer;
